import React, { useState } from 'react';
import axios from 'axios';

function CheckoutPage({ cart, setCart, user }) {
  const [address, setAddress] = useState('');
  const [city, setCity] = useState('');
  const [postalCode, setPostalCode] = useState('');
  const [country, setCountry] = useState('');
  const [message, setMessage] = useState('');

  const handleOrder = async (e) => {
    e.preventDefault();
    if (!user) {
      setMessage('You must be logged in to place an order.');
      return;
    }
    try {
      const token = localStorage.getItem('token');
      await axios.post(
        'http://localhost:5000/api/orders',
        {
          orderItems: cart.map(item => ({
            product: item._id,
            name: item.name,
            qty: item.qty,
            price: item.price,
            image: item.image
          })),
          shippingAddress: { address, city, postalCode, country },
          paymentMethod: 'Cash on Delivery',
          totalPrice: cart.reduce((sum, item) => sum + item.price * item.qty, 0)
        },
        {
          headers: { Authorization: `Bearer ${token}` }
        }
      );
      setCart([]);
      setMessage('Order placed successfully!');
    } catch (err) {
      setMessage(err.response?.data?.message || 'Order failed');
    }
  };

  return (
    <div>
      <h2>Checkout</h2>
      <form onSubmit={handleOrder}>
        <input type="text" placeholder="Address" value={address} onChange={e => setAddress(e.target.value)} required />
        <input type="text" placeholder="City" value={city} onChange={e => setCity(e.target.value)} required />
        <input type="text" placeholder="Postal Code" value={postalCode} onChange={e => setPostalCode(e.target.value)} required />
        <input type="text" placeholder="Country" value={country} onChange={e => setCountry(e.target.value)} required />
        <button type="submit">Place Order</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}

export default CheckoutPage;
