import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';

function Navbar({ user, onLogout, cart }) {
  const location = useLocation();
  const isHome = location.pathname === '/';
  const [menuOpen, setMenuOpen] = useState(false);
  const handleHamburgerClick = () => setMenuOpen((open) => !open);

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <Link to="/">ShopZilla</Link>
      </div>
      <div className="navbar-center">
        <input
          type="text"
          className="search-bar"
          placeholder="Search products..."
          // onChange={...} // You can add search logic here
        />
      </div>
      {/* SVG Hamburger icon for mobile */}
      <button
        className="navbar-hamburger"
        aria-label="Menu"
        onClick={handleHamburgerClick}
        style={{ display: 'none' }}
      >
        <svg xmlns="http://www.w3.org/2000/svg" width="28" height="28" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="lucide lucide-align-justify-icon lucide-align-justify"><path d="M3 12h18"/><path d="M3 18h18"/><path d="M3 6h18"/></svg>
      </button>
      {/* Dropdown menu for mobile */}
      <div className={`navbar-dropdown${menuOpen ? ' open' : ''}`}>
        {user ? (
          <>
            <Link to="/cart" onClick={() => setMenuOpen(false)}>Cart ({cart.length})</Link>
            <Link to="/orders" onClick={() => setMenuOpen(false)}>My Orders</Link>
            <button onClick={() => { setMenuOpen(false); onLogout(); }}>Logout</button>
          </>
        ) : (
          isHome && <Link style={{ marginLeft: 8 }} to="/login" onClick={() => setMenuOpen(false)}>Login</Link>
        )}
      </div>
      {/* Desktop navbar-right */}
      <div className="navbar-right">
        {!isHome && <Link to="/">Home</Link>}
        <Link to="/cart" style={{ marginLeft: 12 }}>Cart ({cart.length})</Link>
        {user ? (
          <>
            <Link to="/orders" style={{ marginLeft: 8 }}>My Orders</Link>
            <button style={{ marginLeft: 8 }} onClick={onLogout}>Logout</button>
          </>
        ) : (
          isHome && <Link style={{ marginLeft: 8 }} to="/login">Login</Link>
        )}
      </div>
    </nav>
  );
}

export default Navbar;
