import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';


function ProductPage({ cart, setCart }) {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [qty, setQty] = useState(1);

  useEffect(() => {
    axios.get(`http://localhost:5000/api/products/${id}`)
      .then(res => setProduct(res.data))
      .catch(err => console.error(err));
  }, [id]);

  if (!product) return <div>Loading...</div>;

  const addToCart = () => {
    const exists = cart.find(item => item._id === product._id);
    if (exists) {
      setCart(cart.map(item => item._id === product._id ? { ...item, qty: item.qty + qty } : item));
    } else {
      setCart([...cart, { ...product, qty }]);
    }
  };

  return (
    <div>
      <h2>{product.name}</h2>
      <img src={product.image || 'https://via.placeholder.com/300'} alt={product.name} style={{ width: 300 }} />
      <p>{product.description}</p>
      <p>Price: ${product.price}</p>
      <p>Stock: {product.countInStock}</p>
      <div>
        <label>Qty: </label>
        <input type="number" min="1" max={product.countInStock} value={qty} onChange={e => setQty(Number(e.target.value))} />
        <button onClick={addToCart} disabled={product.countInStock === 0}>Add to Cart</button>
      </div>
    </div>
  );
}

export default ProductPage;
