import React, { useState, useEffect } from 'react';

function CartPage({ cart, setCart }) {
  const [total, setTotal] = useState(0);

  useEffect(() => {
    setTotal(cart.reduce((sum, item) => sum + item.price * item.qty, 0));
  }, [cart]);

  const removeFromCart = (id) => {
    setCart(cart.filter(item => item._id !== id));
  };

  return (
    <div>
      <h2>Your Cart</h2>
      {cart.length === 0 ? <p>Cart is empty</p> : (
        <div>
          {cart.map(item => (
            <div key={item._id} style={{ border: '1px solid #ccc', margin: 8, padding: 8 }}>
              <h4>{item.name}</h4>
              <p>Qty: {item.qty}</p>
              <p>Price: ${item.price}</p>
              <button onClick={() => removeFromCart(item._id)}>Remove</button>
            </div>
          ))}
          <h3>Total: ${total}</h3>
          <a href="/checkout" className="btn btn-success" style={{ marginTop: '1rem', display: 'inline-block' }}>Proceed to Checkout</a>
        </div>
      )}
    </div>
  );
}

export default CartPage;
