import React, { useEffect, useState } from 'react';
import axios from 'axios';

// Representative images for some common categories
const categoryImages = {
  'Men Clothing': 'https://images.unsplash.com/photo-1512436991641-6745cdb1723f?auto=format&fit=crop&w=600&q=80',
  'Women Clothing': 'https://images.unsplash.com/photo-1517841905240-472988babdf9?auto=format&fit=crop&w=600&q=80',
  'Accessories': 'https://images.unsplash.com/photo-1465101046530-73398c7f28ca?auto=format&fit=crop&w=600&q=80',
  'Electronics': 'https://images.unsplash.com/photo-1519389950473-47ba0277781c?auto=format&fit=crop&w=600&q=80',
  'Audio': 'https://images.unsplash.com/photo-1511367461989-f85a21fda167?auto=format&fit=crop&w=600&q=80',
  'Computers': 'https://images.unsplash.com/photo-1517336714731-489689fd1ca8?auto=format&fit=crop&w=600&q=80',
  'Cameras': 'https://images.unsplash.com/photo-1519125323398-675f0ddb6308?auto=format&fit=crop&w=600&q=80',
  'Footwear': 'https://images.unsplash.com/photo-1519864600265-abb23847ef2c?auto=format&fit=crop&w=600&q=80',
  'Wearables': 'https://images.unsplash.com/photo-1516574187841-cb9cc2ca948b?auto=format&fit=crop&w=600&q=80',
};

function HomePage() {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/products')
      .then(res => {
        // Get unique categories from products
        const cats = Array.from(new Set(res.data.map(p => p.category)));
        setCategories(cats);
      })
      .catch(err => console.error(err));
  }, []);

  return (
    <div className="container mt-4">
      <h2>Shop by Category</h2>
      <div className="row">
        {categories.map(category => (
          <div key={category} className="col-12 col-sm-6 col-md-4 mb-4 d-flex">
            <a
              href={`/products?category=${encodeURIComponent(category)}`}
              className="category-card text-decoration-none"
              aria-label={`Shop ${category} collection`}
              style={{ flex: 1, display: 'flex', flexDirection: 'column', alignItems: 'center', boxShadow: '0 2px 8px rgba(0,0,0,0.07)', borderRadius: 12, background: '#fff', padding: 16, textAlign: 'center', transition: 'box-shadow 0.2s', border: '1px solid #eee' }}
            >
              <img
                src={categoryImages[category] || 'https://via.placeholder.com/300x200?text=Category'}
                alt={category + ' image'}
                className="category-img"
                style={{ width: '100%', height: 180, objectFit: 'cover', borderRadius: 8, marginBottom: 12 }}
              />
              <div className="category-title" style={{ fontWeight: 700, fontSize: 22, color: '#222', marginTop: 8 }}>{category.replace('Clothing', '').trim()}</div>
            </a>
          </div>
        ))}
      </div>
    </div>
  );
}

export default HomePage;
