import React, { useEffect, useState } from 'react';
import axios from 'axios';

function AdminProductsPage({ user }) {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState('');
  const [form, setForm] = useState({ name: '', description: '', price: '', image: '', countInStock: '', category: '' });
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    if (!user || !user.isAdmin) return;
    fetchProducts();
    // eslint-disable-next-line
  }, [user]);

  const fetchProducts = () => {
    const token = localStorage.getItem('token');
    axios.get('http://localhost:5000/api/products', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => setProducts(res.data))
      .catch(err => setError(err.response?.data?.message || 'Failed to fetch products'));
  };

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    try {
      if (editId) {
        await axios.put(`http://localhost:5000/api/products/${editId}` , form, { headers: { Authorization: `Bearer ${token}` } });
      } else {
        await axios.post('http://localhost:5000/api/products', form, { headers: { Authorization: `Bearer ${token}` } });
      }
      setForm({ name: '', description: '', price: '', image: '', countInStock: '', category: '' });
      setEditId(null);
      fetchProducts();
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to save product');
    }
  };

  const handleEdit = product => {
    setForm({ ...product });
    setEditId(product._id);
  };

  const handleDelete = async id => {
    if (!window.confirm('Delete this product?')) return;
    const token = localStorage.getItem('token');
    try {
      await axios.delete(`http://localhost:5000/api/products/${id}`, { headers: { Authorization: `Bearer ${token}` } });
      fetchProducts();
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to delete product');
    }
  };

  if (!user || !user.isAdmin) return <div>Admin access required.</div>;

  return (
    <div className="admin-orders-container">
      <h2>Admin Product Management</h2>
      {error && <p style={{color:'red'}}>{error}</p>}
      <form onSubmit={handleSubmit} style={{marginBottom:24, background:'#f7f7f7', padding:16, borderRadius:8}}>
        <input name="name" placeholder="Name" value={form.name} onChange={handleChange} required />
        <input name="description" placeholder="Description" value={form.description} onChange={handleChange} required />
        <input name="price" type="number" placeholder="Price" value={form.price} onChange={handleChange} required />
        <input name="image" placeholder="Image URL" value={form.image} onChange={handleChange} required />
        <input name="countInStock" type="number" placeholder="Stock" value={form.countInStock} onChange={handleChange} required />
        <input name="category" placeholder="Category" value={form.category} onChange={handleChange} required />
        <button type="submit">{editId ? 'Update' : 'Add'} Product</button>
        {editId && <button type="button" onClick={()=>{setEditId(null);setForm({ name: '', description: '', price: '', image: '', countInStock: '', category: '' });}}>Cancel</button>}
      </form>
      <h3>All Products</h3>
      <div style={{overflowX:'auto'}}>
        <table style={{width:'100%', background:'#fff', borderRadius:8}}>
          <thead>
            <tr>
              <th>Name</th>
              <th>Description</th>
              <th>Price</th>
              <th>Image</th>
              <th>Stock</th>
              <th>Category</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {products.map(product => (
              <tr key={product._id}>
                <td>{product.name}</td>
                <td>{product.description}</td>
                <td>â‚¹{product.price}</td>
                <td><img src={product.image} alt={product.name} style={{width:50, height:50, objectFit:'cover'}} /></td>
                <td>{product.countInStock}</td>
                <td>{product.category}</td>
                <td>
                  <button onClick={()=>handleEdit(product)} style={{marginRight:8}}>Edit</button>
                  <button onClick={()=>handleDelete(product._id)} style={{background:'#d32f2f', color:'#fff'}}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default AdminProductsPage;
