import React, { useState, useEffect } from 'react';

function CartPage({ cart, setCart }) {
  const [total, setTotal] = useState(0);

  useEffect(() => {
    setTotal(cart.reduce((sum, item) => sum + item.price * item.qty, 0));
  }, [cart]);

  const removeFromCart = (id) => {
    setCart(cart.filter(item => item._id !== id));
  };

  return (
    <div className="checkout-container" style={{ maxWidth: 700, margin: '3rem auto', background: '#fff', borderRadius: 12, boxShadow: '0 4px 24px rgba(0,0,0,0.08)', padding: '2.5rem 2rem' }}>
      <h2 style={{ textAlign: 'center', marginBottom: 32 }}>Your Cart</h2>
      {cart.length === 0 ? (
        <div style={{ textAlign: 'center', color: '#888', fontSize: 20 }}>Cart is empty</div>
      ) : (
        <div style={{ display: 'flex', flexDirection: 'column', gap: 24 }}>
          <div style={{ display: 'flex', flexDirection: 'column', gap: 18 }}>
            {cart.map(item => (
              <div key={item._id} className="cart-item" style={{ display: 'flex', alignItems: 'center', gap: 18, boxShadow: '0 2px 8px rgba(0,0,0,0.07)', borderRadius: 8, border: '1px solid #eee', padding: 16, background: '#fafbfc' }}>
                <img src={item.image} alt={item.name} style={{ width: 80, height: 80, objectFit: 'cover', borderRadius: 8, background: '#f7f7f7', marginRight: 16 }} />
                <div style={{ flex: 2 }}>
                  <div style={{ fontWeight: 600, fontSize: 18 }}>{item.name}</div>
                  <div style={{ color: '#666', fontSize: 15 }}>Qty: <span style={{ fontWeight: 500 }}>{item.qty}</span></div>
                  <div style={{ color: '#007bff', fontWeight: 600, fontSize: 16 }}>Price: ₹{item.price}</div>
                </div>
                <button className="btn" style={{ background: '#d32f2f', color: '#fff', borderRadius: 6, fontWeight: 600, padding: '0.5rem 1rem', border: 'none', cursor: 'pointer' }} onClick={() => removeFromCart(item._id)}>
                  Remove
                </button>
              </div>
            ))}
          </div>
          <div style={{ marginTop: 24, background: '#f7f7f7', borderRadius: 10, padding: 24, boxShadow: '0 2px 8px rgba(0,0,0,0.04)', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <div style={{ fontSize: 22, fontWeight: 700, marginBottom: 10 }}>Total: <span style={{ color: '#007bff' }}>₹{total}</span></div>
            <a href="/checkout" className="btn btn-success" style={{ marginTop: 10, fontSize: 18, padding: '0.7rem 2.2rem', borderRadius: 8 }}>Proceed to Checkout</a>
          </div>
        </div>
      )}
    </div>
  );
}

export default CartPage;
