import React, { useEffect, useState } from 'react';
import axios from 'axios';



function HomePage() {
  const [products, setProducts] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('All');

  useEffect(() => {
    axios.get('http://localhost:5000/api/products')
      .then(res => setProducts(res.data))
      .catch(err => console.error(err));
  }, []);

  // Categorize products
  const categories = {};
  products.forEach(product => {
    const cat = product.category || 'Other';
    if (!categories[cat]) categories[cat] = [];
    categories[cat].push(product);
  });
  const categoryList = ['All', ...Object.keys(categories)];

  // Filter products by selected category
  const displayedProducts =
    selectedCategory === 'All'
      ? products
      : categories[selectedCategory] || [];

  return (
    <div>
      <h2>ShopZilla Products</h2>
      <div style={{ marginBottom: '1.5rem', display: 'flex', gap: '1rem', flexWrap: 'wrap' }}>
        {categoryList.map(category => (
          <button
            key={category}
            style={{
              padding: '0.5rem 1.2rem',
              borderRadius: 20,
              border: selectedCategory === category ? '2px solid #007bff' : '1px solid #ccc',
              background: selectedCategory === category ? '#007bff' : '#fff',
              color: selectedCategory === category ? '#fff' : '#222',
              fontWeight: 600,
              cursor: 'pointer',
              marginBottom: 8
            }}
            onClick={() => setSelectedCategory(category)}
          >
            {category}
          </button>
        ))}
      </div>
      <div className="product-list">
        {displayedProducts.map(product => (
          <div key={product._id} className="product-card">
            <img src={product.image || 'https://via.placeholder.com/150'} alt={product.name} />
            <h4>{product.name}</h4>
            <p>â‚¹{product.price}</p>
            <a href={`/product/${product._id}`}>View</a>
          </div>
        ))}
      </div>
    </div>
  );
}

export default HomePage;
